// Helper function to get lesson content translation
export function lessonT(key: string, lang: string): string {
    const keys = key.split(".");
    let value: any = lessonContentTranslations[lang] || lessonContentTranslations["en"];

    for (const k of keys) {
        if (value && value[k]) {
            value = value[k];
        } else {
            // Fallback to English
            value = lessonContentTranslations["en"];
            for (const fallbackKey of keys) {
                if (value && value[fallbackKey]) {
                    value = value[fallbackKey];
                } else {
                    return key;
                }
            }
            break;
        }
    }

    return typeof value === "string" ? value : key;
}
